INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32807, '运维开发工程师', '18-36万', '非凡维格科技', 'https://www.liepin.com/job/1912379119.shtml', '猎聘-其他', '1年以上', '北京', '本科及以上', '1.负责运维系统开发以及工具的设计，如监控数据分析系统、自动部署系统、集群管理系统、数据资产管理系统等；2.针对运维领域已有的系统和环境，以标准化自动化为结果导向，保证稳定性的同时持续优化提升效率；3.协同架构团队/运维团队，改进和优化运维工作，并进行前瞻性规划，负责需求分析/架构设计/模块开发；4.分析运维工作中的瓶颈问题，设计并开发使用于于公司环境的工具，持续提升自动化的比例和效率；5.运维标准化建设，积累并总结运维工作的最佳实践，参与制定标准化相关规范和策略，并跟进实施。岗位要求：1.本科及以上学历，计算机应用，计算机科学技术，计算机网络、软件工程、电子技术、自动化、电子商务、应用数学等理工类相关专业为佳；3.了解Linux操作系统原理，有较长使用时间或系统管理经验为佳，在Linux环境下有完整的项目开发经验为加分项，有内核开发经验为加分项，熟练使用版本管理工具如svn/git/mercurial等为加分项；4.能够熟练使用c/c++/java/php/python/javascript/ruby中至少两门编程语言，有相关领域已完成的项目为加分项，算法和数据结构方面的掌握度为加分项；5.对主流数据库，如MySQL/PostgreSQL/MongoDB，具备基本了解和使用经验，有基于数据的成熟系统开发经验为加分项，有消息队列或中间件的开发使用经验为加分项；6.较强的学习能力和顺畅的沟通能力，具备良好团队协作能力，能够与业务人员对场景/问题/需求进行高效沟通。', '2018年04月12日', '2018-04-13 17:34:55', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32808, 'Android架构师', '14000-20000元/月 ', '北京国电通网络技术有限公司', 'http://jobs.zhaopin.com/215430012251411.htm', '国企', '5-10年', '北京', '本科', '岗位职责：1、参与概要设计，编写模块详细设计文档，和相关技术资料；2、负责重要和核心部分代码编写、对开发内容的单元测试、集成测试；3、负责解决开发过程中的技术问题。任职要求：1、至少5年以上Android平台开发经验，精通Java语言，熟悉多语言开发；2、对软件架构设计有独到见解，曾主导过软件架构设计；3、深入理解目前主流框架原理以及内部实现机制；4、熟练掌握面向对象设计方法和常用设计模式；5、熟悉常用算法和数据结构,熟悉多线程编程；6、熟悉常用的网络通讯方式和协议；7、熟悉Android应用开发框架，能独立开发高性能的Android应用；8. 熟悉Android平台模块化和组件化开发模式；能力素质要求：1、视野开阔，具备技术前瞻性，有较强分析和解决疑难问题的能力；2、学习能力强，有创造性思维能力，对移动新技术持有敏感性并愿意致力于新技术的探索和研究；3、富有团队精神,责任感和沟通能力。', '0002-01-01 00:00:00', '2018-04-13 17:34:56', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32809, '高级测试工程师', '15-23万', '汇通金财信息科技北京', 'https://www.liepin.com/job/1913449844.shtml', '猎聘-其他', '4年以上', '北京', '统招本科', '职位描述：1.负责公司web端、移动端等各个产品线测试需求；2.制定测试计划，编写性能测试脚本，部署、执行性能测试、回归测试等；3.对测试结果进行分析、定位性能瓶颈，推动跟踪问题解决；4.对性能测试数据进行整理分析并提交分析报告；5.对工作流程提出优化和改善建议；职位要求：1.计算机及相关专业本科以上学历，具备4年以上功能、性能测试经验，具有扎实的计算机专业知识；2.熟悉性能测试整体流程、测试方法，具备性能测试需求分析能力；3.熟悉java、shell、python等(其中两种，必需)，可以完成部分脚本的编写，辅助测试的进行;4.熟练使用loadrunner、Jmeter等性能测试工具中的一种或多种，有较强的编写脚本能力；5.熟悉windows或linux的基本操作，命令和配置，了解网络协议和网络环境的应用；6.熟悉Oracle数据库和SQL语言基础；7.掌握接口测试常用工具的使用优先考虑，jmeter、soapui、robotframwork...任意一种；8.具备高度的责任心和敬业精神，有良好的团队合作精神；', '2018年04月12日', '2018-04-13 17:34:56', null);
