INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32812, 'PHP研发', '10001-15000元/月 ', '北京臂湾科技有限公司', 'http://jobs.zhaopin.com/CC378834783J00028097612.htm', '民营', '3-5年', '北京', '大专', '【岗位职责】 - 参与项目需求分析和讨论； - 数据库设计及脚本撰写； - 完成项目相关需求的开发，自测及性能优化工作； - 系统中BUG的修改； - 编写概要设计文档、测试文档等相关技术文档。 【任职资格】- 计算机及其相关专业，本科学历，3年以上工作经验； - 熟练php/python等编程语言，精通PHP语言，面向对象； - 熟悉并掌握一些php主流框架，对并发、大数据有一定处理经验，有APP接口开发经验； - 熟练使用html+css+js语言，熟练jquery,ajax,xml,less等相关技术；对界面交互有一定的必得和技术掌握能力； - 熟练使用常用数据库，有redis、mongodb开发经验，对数据库优化有经验； - 熟练使用Linux的基本操作命令，熟悉WEB服务环境常用配置；- 有java经验优先；', '2018-04-13 13:27:42', '2018-04-13 17:35:03', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32813, '搜索引擎开发工程师（上地）', '20000-25000元/月 ', '厦门一品威客网络科技股份有限公司', 'http://jobs.zhaopin.com/383511923250399.htm', '股份制企业', '3-5年', '北京', '本科', '岗位职责：1、负责突破搜索技术的研究、难点攻关、代码规范；2、负责参与公司核心搜索系统的架构设计，技术改进与性能优化；3、负责搜索引擎相关系统的研发工作，搜索算法优化、改进搜索质量，提升用户体验；任职要求：1、计算机或相关专业本科以上学历；三年以上互联网软件领域的搜索引擎开发经验;2、熟悉Solr/Lucence等开源搜索平台方面有较多实践；在分词技术、文本分类，自然语言处理，信息提取，搜索结果去重、分布式计算等领域有较多研究。3、熟练掌握Java语言，具备良好的面向对象设计和逻辑思维能力4、有责任心, 学习能力强, 工作积极主动, 思路清晰，擅长沟通、有团队合作精神；5、具有使用自然语言处理、机器学习等人工智能算法经验者优先', '0002-01-01 00:00:00', '2018-04-13 17:35:08', null);
INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32814, '高级测试/质量保障工程师', '6000-12000元/月 ', '南昌欧珀电子有限公司', 'http://jobs.zhaopin.com/CC551149020J00090278607.htm', '民营', '3-5年', '北京', '本科', '岗位职责：         1、系统测试用例设计，测试用例编写，代码编写；         2、测试的同级评审以及初级测试人员的工作指导；         3、测试工具的应用、测试工作分析评估；         4、测试技术和测试工具的研究及引入。岗位要求：  1、  熟练掌握软件测试的方法和技巧，能独立制定测试计划、测试用例、测试规      范, 并能编制规范、编写测试用例；  2、  有一定的编码能力，熟悉Java或Python、熟悉常用测试工具和测试框架，有             Jenkins，Selenium, appium或robotium经验优先；      3、有接口测试，web测试经验优先；      4、独立完成功能自动化（UI、接口）环境搭建，自动输出报告；      5、有自动化测试持续集成经验优先，了解安卓、IOS自动化测试。', '2018-04-13 14:31:15', '2018-04-13 17:35:09', null);
