INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32816, '爬虫工程师（上地）', '20000-25000元/月 ', '厦门一品威客网络科技股份有限公司', 'http://jobs.zhaopin.com/383511923250400.htm', '股份制企业', '1-3年', '北京', '本科', '岗位职责：1、编写大规模分布式网络爬虫（多线程、分布式调度、协议处理）；2、能用系统解决动态网页内容抓取、深度网页内容抓取的关键技术；3、采用机器学习，信息检索，大规模数据挖掘等工作。4、爬虫核心算法和策略优化,熟悉采集系统的调度策略，分析爬虫系统瓶颈设计实现相应策略和算法，提升爬虫的抓取效率和质量；任职要求：1、计算机等相关专业优先考虑，本科学历，两年及以上经验。2、精通Python/Java中编程语言中的至少一种；3、熟悉MySql、MongoDB一种或多种数据库开发技术，有较好的数据库设计能力；4、精通网页抓取原理及技术、深度抓取、动态网页技术抓取、浏览器模拟抓取技术，从结构化的和非结构化的数据中获取信息；5、精通爬虫和反爬技术，精通http底层协议；精通深度抓取、动态网页技术抓取、浏览器模拟抓取技术等；6、熟悉分布式系统、多线程 ;7、具有网络爬虫、网页信息抽取、数据挖掘、自然语言处理、信息检索、机器学习、web挖掘等搜索引擎等相关经验优先。8、有图形识别与处理项目经验优先；有非结构化文本提取项目经验优先。', '0002-01-01 00:00:00', '2018-04-13 17:35:11', null);
