INSERT INTO job_data (id, name, pay, company_name, url, company_type, exp, address, education, des, publish_time, collect_time, is_apply) VALUES (32864, '大数据平台开发工程师', '10001-15000元/月 ', '北京兰博新科信息技术有限公司', 'http://jobs.zhaopin.com/CC275484082J00034131915.htm', '民营', '不限', '北京', '本科', '岗位职责：1、参与银行、政府、电信的大数据平台（数仓）建设2、CDH/HDP集群的搭建和部署，组件的选型和适配3、Hive存储过程开发（HPLSQL），迁移ORACLE PLSQL模型到Hive（高兼容性）4、HUE和Ambari/ClouderaManager的二次开发，以适应个性化需求5、使用Sqoop+Oozie开发ETL工作流，并进行数据迁移任职要求：	基本项	1、良好的工作态度和责任心，稳定，能接受出差	2、统招本科以上学历，计算机/软件相关专业	3、掌握Java/Python/R语言，熟悉Linux环境和shell编写	4、熟悉常见关系型数据库(MySQL/Oracle/DB2等)，能够编写复杂SQL/存储过程，并运用查询分析器进行SQL优化	5、熟练使用git/maven进行版本控制和项目构建		重点项：	1、参与过真实大数据项目，数据规模在TB级别	2、熟悉数据仓库和OLAP的概念和操作	3、熟悉Hadoop生态体系，对CDH/HDP/TDH有一定的应用经验，有一定架构、部署和运维能力	4、熟悉Hadoop相关组件，如Hive、Spark、Oozie、Sqoop、Atlas	5、熟悉Ambari/ClouderaManager和HUE/Zeppelin，并能对其进行二次开发，从而搭建完整的大数据管理和分析平台	6、熟悉Hive上存储过程的开发，有一定的Hive/HPLSQL源码修改能力	7、熟悉多种SQL on Hadoop技术，了解各种技术的优缺点和适用场景，如哪些适合跑批哪些适合即席查询，哪些SQL标准兼容性高	加分项：	1、参与过完整大数据平台的搭建和开发，包括对服务/集群的管理，并支持SQL/存储过程/R语言的BI分析工具	2、参与过银行大数据项目（平台型，而非业务型）	3、熟悉HPLSQL，并能够在Hive上开发存储过程（或迁移PLSQL存储过程）	4、熟悉Antlr，能够对语法进行定义、解析和翻译	5、有可演示成果物	6、了解Druid或incubator-superset，能够用其搭建BI分析平台	7、了解ORACLE BIG DATA SQL', '2018-04-13 11:13:51', '2018-04-13 17:36:26', null);
