DROP VIEW v_applay_job;
CREATE VIEW v_applay_job (id, name, pay, company_name, publish_time, collect_time, is_apply, url) AS select `job_data`.`id` AS `id`,`job_data`.`name` AS `name`,`job_data`.`pay` AS `pay`,`job_data`.`company_name` AS `company_name`,`job_data`.`publish_time` AS `publish_time`,`job_data`.`collect_time` AS `collect_time`,`job_data`.`is_apply` AS `is_apply`,`job_data`.`url` AS `url` from `job_data` where (`job_data`.`is_apply` = '1');
DROP VIEW v_new_company;
CREATE VIEW v_new_company (name, company_type, isconside) AS select `conside_company`.`name` AS `name`,`conside_company`.`company_type` AS `company_type`,`conside_company`.`isconside` AS `isconside` from `conside_company` where isnull(`conside_company`.`isconside`);
DROP VIEW v_new_job;
CREATE VIEW v_new_job (id, name, pay, company_name, publish_time, collect_time, is_apply, url, des) AS select `job_data`.`id` AS `id`,`job_data`.`name` AS `name`,`job_data`.`pay` AS `pay`,`job_data`.`company_name` AS `company_name`,`job_data`.`publish_time` AS `publish_time`,`job_data`.`collect_time` AS `collect_time`,`job_data`.`is_apply` AS `is_apply`,`job_data`.`url` AS `url`,`job_data`.`des` AS `des` from `job_data` where (((`job_data`.`publish_time` <> '0') and ((`job_data`.`is_apply` <> '1') or isnull(`job_data`.`is_apply`))) or ((`job_data`.`publish_time` = '9') and isnull(`job_data`.`is_apply`)));
